{% set data = frappe.get_all('Item Barcode Table', {'parent': doc.name}, ['item_code', 'item_name', 'batch_no', 'barcode', 'number_of_print']) %}

{% for item in data %}
    {% set item_price = frappe.get_all('Item Price', {'item_code': item.item_code, 'selling': 1}, ['price_list_rate']) %}

    <div>SIZE 78.6 mm, 25.4 mm</div>
    <div>GAP 3 mm, 0 mm</div>
    <div>DIRECTION 0,0</div>
    <div>REFERENCE 0,0</div>
    <div>OFFSET 0 mm</div>
    <div>SET PEEL OFF</div>
    <div>SET CUTTER OFF</div>
    <div>SET PARTIAL_CUTTER OFF</div>
    <div>SET TEAR ON</div>
    <div>CLS</div>
    <div>CODEPAGE 1252</div>

    <div>TEXT 528,205,"ROMAN.TTF",180,1,7,"FILLOMART"</div>
    <div>TEXT 615,45,"ROMAN.TTF",90,1,10,"Rs.{{ item_price[0].price_list_rate if item_price else "00.00" }}"</div>
    {% if item.batch_no %}
    {% set barcode= item.batch_no %}
    {% elif item.barcode %}
    {% set barcode= item.barcode %}
    {% else %}
    {% set barcode= item.item_code%}
    {% endif%}
    {% if barcode |length == 13 %}
    <div>BARCODE 580,90,"EAN13",35,0,180,2,4,"{{ item.batch_no or item.barcode or item.item_code }}"</div>
    {% elif barcode | length <= 12 %}
    <div>BARCODE 580,90,"128M",35,0,180,2,4,"!105{{ item.batch_no or item.barcode or item.item_code }}"</div>
    {% endif %}
    <div>TEXT 538,49,"ROMAN.TTF",180,1,7,"{{ item.batch_no or item.barcode or item.item_code }}"</div>
    <div>TEXT 580,170,"ROMAN.TTF",180,1,6,"BEACON DRIVE PRIVATE LIMITED"</div>
    <div>TEXT 557,147,"ROMAN.TTF",180,1,6,"Fssai: 21323212000162"</div>
    <div>TEXT 580,125,"ROMAN.TTF",180,1,6,"{{ item.item_name | upper }}"</div>
    <div>TEXT 534,107,"ROMAN.TTF",180,1,4,"INCLUDING ALL TAXES"</div>
    {% if item.number_of_print > 1 and item.number_of_print % 2 == 0 %}
    <div>TEXT 199,205,"ROMAN.TTF",180,1,7,"FILLOMART"</div>
    <div>TEXT 295,45,"ROMAN.TTF",90,1,10,"Rs.{{ item_price[0].price_list_rate if item_price else "00.00" }}"</div>
    {% if barcode |length == 13 %}
    <div>BARCODE 251,90,"EAN13",35,0,180,2,4,"{{ item.batch_no or item.barcode or item.item_code }}"</div>
    {% elif barcode | length <= 12 %}
    <div>BARCODE 251,90,"128M",35,0,180,2,4,"!105{{ item.batch_no or item.barcode or item.item_code }}"</div>
    {% endif %}
    <div>TEXT 209,49,"ROMAN.TTF",180,1,7,"{{ item.batch_no or item.barcode or item.item_code }}"</div>
    <div>TEXT 251,170,"ROMAN.TTF",180,1,6,"BEACON DRIVE PRIVATE LIMITED"</div>
    <div>TEXT 228,147,"ROMAN.TTF",180,1,6,"Fssai: 21323212000162"</div>
    <div>TEXT 251,125,"ROMAN.TTF",180,1,6,"{{ item.item_name | upper }}"</div>
    <div>TEXT 205,107,"ROMAN.TTF",180,1,4,"INCLUDING ALL TAXES"</div>

    <div>PRINT 1,{{ item.number_of_print/2 | int }}</div>
    {% elif item.number_of_print > 1 and item.number_of_print % 2 != 0 %}
    <div>TEXT 199,205,"ROMAN.TTF",180,1,7,"FILLOMART"</div>
    <div>TEXT 295,45,"ROMAN.TTF",90,1,10,"Rs.{{ item_price[0].price_list_rate if item_price else "00.00" }}"</div>
    {% if barcode |length == 13 %}
    <div>BARCODE 251,90,"EAN13",35,0,180,2,4,"{{ item.batch_no or item.barcode or item.item_code }}"</div>
    {% elif barcode | length <= 12 %}
    <div>BARCODE 251,90,"128M",35,0,180,2,4,"!105{{ item.batch_no or item.barcode or item.item_code }}"</div>
    {% endif %}
    <div>TEXT 209,49,"ROMAN.TTF",180,1,7,"{{ item.batch_no or item.barcode or item.item_code }}"</div>
    <div>TEXT 251,170,"ROMAN.TTF",180,1,6,"BEACON DRIVE PRIVATE LIMITED"</div>
    <div>TEXT 228,147,"ROMAN.TTF",180,1,6,"Fssai: 21323212000162"</div>
    <div>TEXT 251,125,"ROMAN.TTF",180,1,6,"{{ item.item_name | upper }}"</div>
    <div>TEXT 205,107,"ROMAN.TTF",180,1,4,"INCLUDING ALL TAXES"</div>
    <div>PRINT 1,{{ (item.number_of_print+1)/2 | int }}</div>
    {% elif item.number_of_print == 1 %}
    <div>TEXT 199,205,"ROMAN.TTF",180,1,7,"FILLOMART"</div>
    <div>TEXT 295,45,"ROMAN.TTF",90,1,10,"Rs.{{ item_price[0].price_list_rate if item_price else "00.00" }}"</div>
    {% if barcode |length == 13 %}
    <div>BARCODE 251,90,"EAN13",35,0,180,2,4,"{{ item.batch_no or item.barcode or item.item_code }}"</div>
    {% elif barcode | length <= 12 %}
    <div>BARCODE 251,90,"128M",35,0,180,2,4,"!105{{ item.batch_no or item.barcode or item.item_code }}"</div>
    {% endif %}
    <div>TEXT 209,49,"ROMAN.TTF",180,1,7,"{{ item.batch_no or item.barcode or item.item_code }}"</div>
    <div>TEXT 251,170,"ROMAN.TTF",180,1,6,"BEACON DRIVE PRIVATE LIMITED"</div>
    <div>TEXT 228,147,"ROMAN.TTF",180,1,6,"Fssai: 21323212000162"</div>
    <div>TEXT 251,125,"ROMAN.TTF",180,1,6,"{{ item.item_name | upper }}"</div>
    <div>TEXT 205,107,"ROMAN.TTF",180,1,4,"INCLUDING ALL TAXES"</div>
    <div>PRINT 1,1</div>
    {% endif %}
{% endfor %}
